mapper.select:
select b.log_id as logId,b.user_log_noise_level as userLogNoiseLevel,b.audit_log_noise_level as auditLogNoiseLevel,b.user_user_id as userUserId,b.audit_user_id as auditUserId,b.user_ano_status as userAnoStatus ,b.audit_ano_status as auditAnoStatus from (SELECT c.log_id, c.log_noise_level as user_log_noise_level, f.log_noise_level as audit_log_noise_level, c.user_id as user_user_id, f.user_id as audit_user_id, f.ano_status as audit_ano_status, c.ano_status as user_ano_status from alert_log_user c INNER JOIN (SELECT a.p_id ,count(a.p_id) as num from ano_user a INNER JOIN (select p_id from ano_admin
user_id = #{userId}
) b on a.p_id =b.p_id GROUP BY a.p_id HAVING num = 2) d on c.p_id = d.p_id LEFT JOIN (select user_id ,ano_status ,log_noise_level,log_id from alert_log_audit) f on f.log_id = c.log_id ORDER BY c.log_id desc ) b
b.log_id = #{logId}
b.audit_ano_status is not null
b.audit_ano_status is null

mapper.update:
update alert_log_audit
log_noise = #{logNoise}, log_noise_level = #{logNoiseLevel}, p_id = #{pId}, user_id = #{userId}, log_type = #{logType}, event_name = #{eventName}, event_description = #{eventDescription}, ano_status = #{anoStatus}, data_label = #{dataLabel}, point_label = #{pointLabel},
where log_id = #{logId}
