select DATE_FORMAT(connection_time,'%Y-%m-%d') as date,COUNT(connection_time) as info from patient_management m inner join patient p on p.patient_phone_aes = m.patient_phone_aes inner join equipment e on m.equipment_code = e.equipment_code left join hospital h on m.hospital_code = h.hospital_code left join report r on m.p_id = r.p_id
and ecg_type like concat(#{ecgType}, '%') and m.patient_phone_aes = #{patientPhone}
GROUP BY DATE_FORMAT(connection_time,'%Y-%m-%d')

SELECT COUNT(r.p_phone_aes) as totalCollection
FROM report r INNER JOIN patient_management p  ON r.p_id = p.p_id
WHERE p.connection_time BETWEEN #{startReportTime} and  DATE_ADD(#{endReportTime} ,INTERVAL 1 DAY)and r.p_phone_aes = #{pPhone}

select m.p_id, p.patient_code, m.hospital_code, m.equipment_code, connection_time, bed_number, case_history_number, ecg_type, online_status, patient_name_aes, patient_age, patient_sex, patient_source, m.patient_phone_aes, family_phone, monitoring_status, equipment_status, equipment_type, hospital_name, intelligent_diagnosis, diagnosis_status, diagnosis_conclusion, diagnosis_doctor_aes, report_time, ecg_level, doctor_phone_aes, birth_day, pacemaker, upload_time, guardianship_duration, sampling_rate, detection_state, end_time, time_duration, d_phone_aes, report_state,patient_symptom,TIMESTAMPDIFF(SECOND,start_time,report_time) as avgTime from patient_management m inner join patient p on p.patient_phone_aes = m.patient_phone_aes inner join equipment e on m.equipment_code = e.equipment_code left join hospital h on m.hospital_code = h.hospital_code left join report r on m.p_id = r.p_id
and diagnosis_status = #{diagnosisStatus} and m.p_id = #{pId} and patient_sex like concat(#{patientSex},'%') and patient_name_aes like concat('%', #{patientName}, '%') and p.patient_code like concat('%', #{patientCode}, '%') and m.equipment_code like concat('%', #{equipmentCode}, '%') and connection_time between #{params.beginConnectionTime} and #{params.endConnectionTime} and bed_number like concat('%', #{bedNumber}, '%') and case_history_number like concat('%', #{caseHistoryNumber}, '%') and online_status = #{onlineStatus} and ecg_type like concat(#{ecgType},'%') and hospital_name = #{hospitalName} and m.patient_phone_aes like concat(#{PatPhone},'%') and m.patient_phone_aes = #{patientPhone} and intelligent_diagnosis like concat('%', #{intelligentDiagnosis}, '%') and diagnosis_conclusion like concat('%', #{diagnosisConclusion}, '%') and diagnosis_doctor_aes like concat('%', #{diagnosisDoctor}, '%') and report_time between #{params.beginReportTime} and #{params.endReportTime} and report_time like concat(#{countTime},'%') and ecg_level = #{ecgLevel} and d_phone_aes like concat(#{doctorPhone}, '%') and date_format(connection_time, '%Y-%m-%d') = #{endReportTime} and detection_state = #{detectionState}
and m.equipment_code in
#{item}
and m.hospital_code in
#{item}
and diagnosis_status=1 and start_time is not null and r.report_normal=1 order by report_time desc

