mapper.select:


     and p_id = #{pId}
     and diagnosis_status = #{diagnosisStatus}
     and diagnosis_conclusion like concat('%', #{diagnosisConclusion}, '%')
     and diagnosis_doctor_aes like concat('%', #{diagnosisDoctor}, '%')
     and report_time between #{startReportTime} and DATE_ADD(#{endReportTime}, INTERVAL 1 DAY) 
     and report_type like concat(#{reportType}, '%')
     and intelligent_diagnosis = #{intelligentDiagnosis}
     and d_phone_aes = #{dPhone}
     and handling_suggestion = #{handlingSuggestion}
     and health_advice = #{healthAdvice}
     and p_phone_aes = #{pPhone}
     and patient_symptom = #{patientSymptom}

order by report_time desc

select report_id, report.p_id, diagnosis_status, diagnosis_conclusion, diagnosis_doctor_aes, report_time, report_type, intelligent_diagnosis, d_phone_aes, handling_suggestion, health_advice, p_phone_aes,patient_symptom,start_time,login_user_phone,refuse_text,report_normal,patient_management.log_type
from report inner join patient_management on report.p_id = patient_management.p_id
where report.p_id = #{pId}

mapper.update:
update report
p_id = #{pId}, diagnosis_status = #{diagnosisStatus}, diagnosis_conclusion = #{diagnosisConclusion}, diagnosis_doctor_aes = #{diagnosisDoctor}, report_time = #{reportTime}, start_time = #{startTime}, report_type = #{reportType}, intelligent_diagnosis = #{intelligentDiagnosis}, d_phone_aes = #{dPhone}, handling_suggestion = #{handlingSuggestion}, health_advice = #{healthAdvice}, p_phone_aes = #{pPhone}, patient_symptom = #{patientSymptom}, login_user_phone = #{loginUserPhone}, report_normal = #{reportNormal}, refuse_text = #{refuseText},
where report_id = #{reportId}

update report
p_id = #{pId}, diagnosis_status = #{diagnosisStatus}, diagnosis_conclusion = #{diagnosisConclusion}, report_time = #{reportTime}, start_time = #{startTime}, report_type = #{reportType}, intelligent_diagnosis = #{intelligentDiagnosis}, handling_suggestion = #{handlingSuggestion}, health_advice = #{healthAdvice}, p_phone_aes = #{pPhone}, patient_symptom = #{patientSymptom}, login_user_phone = #{loginUserPhone}, report_normal = #{reportNormal}, refuse_text = #{refuseText}, diagnosis_doctor_aes = null, d_phone_aes = null,
where report_id = #{reportId}

update report
diagnosis_status = #{diagnosisStatus}, diagnosis_conclusion = #{diagnosisConclusion}, diagnosis_doctor_aes = #{diagnosisDoctor}, report_time = #{reportTime}, start_time = #{startTime}, report_type = #{reportType}, intelligent_diagnosis = #{intelligentDiagnosis}, d_phone_aes = #{dPhone}, handling_suggestion = #{handlingSuggestion}, health_advice = #{healthAdvice}, p_phone_aes = #{pPhone}, patient_symptom = #{patientSymptom}, login_user_phone = #{loginUserPhone}, report_normal = #{reportNormal}, refuse_text = #{refuseText},
where p_id = #{pId}
