mapper.select:
select c.doctorName,TIMESTAMPDIFF(SECOND,c.b_time,c.f_time)  as timeCount, MONTH(c.f_time) as month
from (
    select r.diagnosis_doctor as doctorName, r.start_time as b_time,r.report_time as f_time from report r
   where MONTH(r.report_time) = #{month} and MONTH(r.start_time) = #{month} and r.diagnosis_doctor is not null and r.diagnosis_status = 1

        and r.d_phone = #{doctorPhone}


        and r.report_type like concat(#{reportType},"%")

    ) as c order by timeCount DESC

select c.doctorName ,sum(TIMESTAMPDIFF(SECOND,c.b_time,c.f_time)) as countTime ,count(c.doctorName) as count ,avg(TIMESTAMPDIFF(SECOND,c.b_time,c.f_time)) as averageTime
from (
         select r.diagnosis_doctor as doctorName, r.start_time as b_time,r.report_time as f_time  from report r
                                                                                                           INNER JOIN doctor d ON r.diagnosis_doctor =  d.doctor_name
         where  r.diagnosis_doctor is not null and r.diagnosis_status = 1  and start_time is not null

                    and  r.report_time BETWEEN #{startTime}  and #{endTime}

     ) as c GROUP BY c.doctorName ORDER BY  averageTime asc
